import{_ as o,c as e,aA as r,o as s}from"./chunks/framework.DyPz110q.js";const u=JSON.parse('{"title":"123. 买卖股票的最佳时机 III","description":"","frontmatter":{"title":"123. 买卖股票的最佳时机 III","categories":"leetcode","tags":["Array","Dynamic Programming"],"date":"2025-02-25T13:51:11.884Z","updated":"2025-02-25T13:51:11.884Z"},"headers":[],"relativePath":"123. 买卖股票的最佳时机 III.md","filePath":"123. 买卖股票的最佳时机 III.md"}'),a={name:"123. 买卖股票的最佳时机 III.md"};function n(p,t,c,l,i,g){return s(),e("div",null,t[0]||(t[0]=[r('<h2 id="题目" tabindex="-1">题目 <a class="header-anchor" href="#题目" aria-label="Permalink to &quot;题目&quot;">​</a></h2><p><a href="https://leetcode.cn/problems/best-time-to-buy-and-sell-stock-iii" target="_blank" rel="noreferrer">123. 买卖股票的最佳时机 III</a></p><p>给定一个数组，它的第 __<code>i</code> 个元素是一支给定的股票在第 <code>i</code> __ 天的价格。</p><p>设计一个算法来计算你所能获取的最大利润。你最多可以完成 <strong>两笔</strong> 交易。</p><p><strong>注意：</strong> 你不能同时参与多笔交易（你必须在再次购买前出售掉之前的股票）。</p><p><strong>示例 1:</strong></p><blockquote><p><strong>输入：</strong> prices = [3,3,5,0,0,3,1,4] <strong>输出：</strong> 6 <strong>解释：</strong> 在第 4 天（股票价格 = 0）的时候买入，在第 6 天（股票价格 = 3）的时候卖出，这笔交易所能获得利润 = 3-0 = 3 。 随后，在第 7 天（股票价格 = 1）的时候买入，在第 8 天 （股票价格 = 4）的时候卖出，这笔交易所能获得利润 = 4-1 = 3 。</p></blockquote><p><strong>示例 2：</strong></p><blockquote><p><strong>输入：</strong> prices = [1,2,3,4,5] <strong>输出：</strong> 4 <strong>解释：</strong> 在第 1 天（股票价格 = 1）的时候买入，在第 5 天 （股票价格 = 5）的时候卖出, 这笔交易所能获得利润 = 5-1 = 4 。 注意你不能在第 1 天和第 2 天接连购买股票，之后再将它们卖出。 因为这样属于同时参与了多笔交易，你必须在再次购买前出售掉之前的股票。</p></blockquote><p><strong>示例 3：</strong></p><blockquote><p><strong>输入：</strong> prices = [7,6,4,3,1] <strong>输出：</strong> 0 <strong>解释：</strong> 在这个情况下, 没有交易完成, 所以最大利润为 0。</p></blockquote><p><strong>示例 4：</strong></p><blockquote><p><strong>输入：</strong> prices = [1] <strong>输出：</strong> 0</p></blockquote><p><strong>提示：</strong></p><ul><li><code>1 &lt;= prices.length &lt;= 105</code></li><li><code>0 &lt;= prices[i] &lt;= 105</code></li></ul><h2 id="思路" tabindex="-1">思路 <a class="header-anchor" href="#思路" aria-label="Permalink to &quot;思路&quot;">​</a></h2><h2 id="题解" tabindex="-1">题解 <a class="header-anchor" href="#题解" aria-label="Permalink to &quot;题解&quot;">​</a></h2><div class="language-cpp vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">cpp</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"></span></code></pre></div>',18)]))}const h=o(a,[["render",n]]);export{u as __pageData,h as default};
